import React, { useState } from "react";
import MahasiswaModal from "./MahasiswaModal";
import MahasiswaTable from "./MahasiswaTable";

const Mahasiswa = () => {
  const [mahasiswa, setMahasiswa] = useState([]);
  const [selectedMahasiswa, setSelectedMahasiswa] = useState(null);
  const [isModalOpen, setModalOpen] = useState(false);

  const storeMahasiswa = (newMahasiswa) => {
    setMahasiswa([...mahasiswa, newMahasiswa]);
  };

  const updateMahasiswa = (updatedMahasiswa) => {
    setMahasiswa(
      mahasiswa.map((m) =>
        m.nim === updatedMahasiswa.nim ? updatedMahasiswa : m
      )
    );
  };

  const deleteMahasiswa = (nim) => {
    setMahasiswa(mahasiswa.filter((m) => m.nim !== nim));
  };

  const openAddModal = () => {
    setSelectedMahasiswa(null);
    setModalOpen(true);
  };

  const openEditModal = (mahasiswa) => {
    setSelectedMahasiswa(mahasiswa);
    setModalOpen(true);
  };

  const handleSubmit = (mahasiswaData) => {
    if (selectedMahasiswa) {
      updateMahasiswa(mahasiswaData);
    } else {
      storeMahasiswa(mahasiswaData);
    }
    setModalOpen(false);
  };

  const handleDelete = (nim) => {
    deleteMahasiswa(nim);
  };

  return (
    <div className="p-4 ">
      <div className="flex justify-between">
        <h1 className="text-xl font-bold mb-4">Data Mahasiswa</h1>
        <button
          onClick={openAddModal}
          className="bg-blue-500 text-white px-4 py-2 rounded mb-4 hover:bg-blue-600"
        >
          Tambah Mahasiswa
        </button>
      </div>
      <MahasiswaTable
        mahasiswa={mahasiswa}
        openEditModal={openEditModal}
        onDelete={handleDelete}
      />
      <MahasiswaModal
        isModalOpen={isModalOpen}
        onClose={() => setModalOpen(false)}
        onSubmit={handleSubmit}
        selectedMahasiswa={selectedMahasiswa}
      />
    </div>
  );
};

export default Mahasiswa;
