import React, { useState, useEffect } from "react";

const FormMahasiswa = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [editData, setEditData] = useState(null);
  const [nim, setNim] = useState("");
  const [nama, setNama] = useState("");
  const [oldNim, setOldNim] = useState("");

  // Fungsi untuk membuka modal
  const handleOpenModal = (data = null) => {
    setEditData(data);
    setIsModalOpen(true);
  };

  // Fungsi untuk menutup modal
  const handleCloseModal = () => {
    setIsModalOpen(false);
    setEditData(null);
    setNim("");
    setNama("");
    setOldNim("");
  };

  useEffect(() => {
    if (editData) {
      setNim(editData.nim);
      setOldNim(editData.nim);
      setNama(editData.nama);
    } else {
      setNim("");
      setNama("");
      setOldNim("");
    }
  }, [editData]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!nim || !nama) {
      alert("NIM dan Nama harus diisi!");
      return;
    }

    const updatedData = { nim, nama };

    if (editData && oldNim !== nim) {
      console.log("Data disimpan (dengan perubahan NIM):", {
        ...updatedData,
        oldNim,
      });
    } else {
      console.log("Data disimpan:", updatedData);
    }

    handleCloseModal(); // Tutup modal setelah submit
  };

  return (
    <div>
      {isModalOpen && (
        <div
          className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50"
          onClick={handleCloseModal} // Klik di luar modal untuk menutup
        >
          <div
            className="bg-white rounded-lg shadow-lg w-full max-w-md p-4"
            onClick={(e) => e.stopPropagation()} // Mencegah modal tertutup saat klik di dalamnya
          >
            {/* Header Modal */}
            <div className="flex justify-between items-center mb-3">
              <h2 className="text-lg font-semibold">
                {editData ? "Edit Mahasiswa" : "Tambah Mahasiswa"}
              </h2>

              <button
                className="text-gray-600 hover:text-red-500 text-2xl"
                onClick={handleCloseModal}
              >
                &times;
              </button>
            </div>

            {/* Form */}
            <form onSubmit={handleSubmit} className="p-4 space-y-4">
              <div>
                <label
                  htmlFor="nim"
                  className="block text-sm font-medium text-gray-700"
                >
                  NIM
                </label>
                <input
                  type="text"
                  id="nim"
                  value={nim}
                  onChange={(e) => setNim(e.target.value)}
                  required
                  className="w-full px-4 py-2 border rounded focus:outline-none focus:ring focus:ring-blue-300"
                />
              </div>
              <div>
                <label
                  htmlFor="nama"
                  className="block text-sm font-medium text-gray-700"
                >
                  Nama
                </label>
                <input
                  type="text"
                  id="nama"
                  value={nama}
                  onChange={(e) => setNama(e.target.value)}
                  required
                  className="w-full px-4 py-2 border rounded focus:outline-none focus:ring focus:ring-blue-300"
                />
              </div>
              <div className="flex justify-end space-x-2 mt-4">
                <button
                  type="button"
                  className="bg-gray-400 text-white px-4 py-2 rounded"
                  onClick={handleCloseModal}
                >
                  Batal
                </button>
                <button
                  type="submit"
                  className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded"
                >
                  {editData ? "Simpan Perubahan" : "Simpan"}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default FormMahasiswa;
