import React from "react";

const Modal = ({ isOpen, onClose, editData, children }) => {
  if (!isOpen) return null;

  const title = editData ? "Edit Mahasiswa" : "Tambah Mahasiswa";

  return (
    <div
      className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50"
      onClick={onClose} // Klik luar modal untuk menutup
    >
      <div
        className="bg-white rounded-lg shadow-lg w-full max-w-md p-4"
        onClick={(e) => e.stopPropagation()} // Mencegah modal tertutup saat klik di dalamnya
      >
        {/* Header Modal */}
        <div className="flex justify-between items-center mb-3">
          <h2 className="text-lg font-semibold">{title}</h2>
          <button
            className="text-gray-600 hover:text-red-500 text-2xl"
            onClick={onClose} // Tombol close
          >
            &times;
          </button>
        </div>

        {/* Isi Modal */}
        {children}
      </div>
    </div>
  );
};

export default Modal;
