// src/components/molecules/FormMatkul.jsx
import React from "react";
import Label from "../atoms/Label";
import Input from "../atoms/Input";
import Button from "../atoms/Button";

const FormMatkul = ({ onSubmit, form, setForm, onClose, editData }) => {
  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit();
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4 p-4">
      <div className="flex justify-between items-center mb-3">
        <h2 className="text-lg font-semibold">
          {editData ? "Edit Mata Kuliah" : "Tambah Mata Kuliah"}
        </h2>
        <button type="button" onClick={onClose} className="text-2xl">&times;</button>
      </div>

      <div>
        <Label htmlFor="kode">Kode</Label>
        <Input
          id="kode"
          name="kode"
          value={form.kode}
          onChange={handleChange}
          required
        />
      </div>

      <div>
        <Label htmlFor="nama">Nama</Label>
        <Input
          id="nama"
          name="nama"
          value={form.nama}
          onChange={handleChange}
          required
        />
      </div>

      <div>
        <Label htmlFor="sks">SKS</Label>
        <Input
          id="sks"
          name="sks"
          type="number"
          value={form.sks}
          onChange={handleChange}
          required
        />
      </div>

      <div className="flex justify-end space-x-2 mt-4">
        <Button type="button" onClick={onClose} className="bg-gray-400 text-white">
          Batal
        </Button>
        <Button type="submit" className="bg-blue-600 hover:bg-blue-700">
          Simpan
        </Button>
      </div>
    </form>
  );
};

export default FormMatkul;
