import React, { useState, useEffect } from "react";
import Label from "../atoms/Label";
import Input from "../atoms/Input";
import Button from "../atoms/Button";

const Modal = ({ onSubmit, editData, onClose }) => {
  const [nim, setNim] = useState("");
  const [nama, setNama] = useState("");

  useEffect(() => {
    if (editData) {
      setNim(editData.nim);
      setNama(editData.nama);
    } else {
      setNim("");
      setNama("");
    }
  }, [editData]);

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!nim.trim() || !nama.trim()) {
      alert("NIM dan Nama harus diisi!");
      return;
    }

    onSubmit({ nim: nim.trim(), nama: nama.trim(), oldNim: editData?.nim });

    setNim("");
    setNama("");
  };

  return (
    <form onSubmit={handleSubmit} className="p-4 space-y-4">
      <h2 className="text-lg font-semibold">
        {editData ? "Edit Mahasiswa" : "Tambah Mahasiswa"}
      </h2>
      <div>
        <Label htmlFor="nim">NIM</Label>
        <Input
          type="text"
          id="nim"
          value={nim}
          onChange={(e) => setNim(e.target.value)}
          required
          className="w-full px-4 py-2 border rounded focus:outline-none focus:ring focus:ring-blue-300"
        />
      </div>
      <div>
        <Label htmlFor="nama">Nama</Label>
        <Input
          type="text"
          id="nama"
          value={nama}
          onChange={(e) => setNama(e.target.value)}
          required
          className="w-full px-4 py-2 border rounded focus:outline-none focus:ring focus:ring-blue-300"
        />
      </div>
      <div className="flex justify-end space-x-2 mt-4">
        <Button className="bg-gray-400 text-white" onClick={onClose}>
          Batal
        </Button>
        <Button type="submit" className="bg-blue-600 hover:bg-blue-700">
          {editData ? "Simpan Perubahan" : "Simpan"}
        </Button>
      </div>
    </form>
  );
};

export default Modal;
