import React, { useState, useEffect } from "react";
import Button from "../components/atoms/Button";
import Modal from "../components/molecules/Modal";
import FormMahasiswa from "../components/molecules/FormMahasiswa";
import Sidebar from "../components/organisms/Sidebar";
import Header from "../components/organisms/Header";
import Footer from "../components/organisms/Footer";
import {
  confirmDeleteMahasiswa,
  confirmUpdateMahasiswa,
} from "../Utils/Helpers/SwalHelpers";
import { toastSuccess, toastError } from "../Utils/Helpers/ToastHelpers";

const Mhs = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [mahasiswa, setMahasiswa] = useState([
    { nim: "2201001", nama: "Aulia Rahma", status: true },
    { nim: "2201002", nama: "Dimas Pratama", status: false },
    { nim: "2201003", nama: "Siti Nurhaliza", status: true },
  ]);
  const [editData, setEditData] = useState(null);
  const [role, setRole] = useState(null);

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem("user"));
    if (!user) {
      window.location.href = "/login";
    } else {
      setRole(user.role);
    }
  }, []);

  const addMahasiswa = (newData) => {
    try {
      setMahasiswa([...mahasiswa, newData]);
      setIsModalOpen(false);
      toastSuccess("Data mahasiswa berhasil disimpan!");
    } catch (error) {
      toastError("Gagal menyimpan data mahasiswa.");
    }
  };

  const updateMahasiswa = async (updatedData) => {
    const result = await confirmUpdateMahasiswa();
    if (result.isConfirmed) {
      try {
        setMahasiswa((prev) =>
          prev.map((mhs) => (mhs.nim === editData.nim ? updatedData : mhs))
        );
        setIsModalOpen(false);
        setEditData(null);
        toastSuccess("Data mahasiswa berhasil diperbarui!");
      } catch (error) {
        toastError("Gagal memperbarui data mahasiswa.");
      }
    }
  };

  const deleteMahasiswa = async (nim) => {
    const target = mahasiswa.find((m) => m.nim === nim);
    const result = await confirmDeleteMahasiswa();
    if (result.isConfirmed) {
      try {
        setMahasiswa(mahasiswa.filter((mhs) => mhs.nim !== nim));
        toastSuccess(`Data ${target.nama} berhasil dihapus.`);
      } catch (error) {
        toastError("Gagal menghapus data mahasiswa.");
      }
    }
  };

  return (
    <div className="flex h-screen">
      <Sidebar />
      <div className="flex-1 flex flex-col">
        <Header />
        <main className="flex-1 p-6 bg-gray-100">
          <div className="bg-white shadow rounded-lg p-4">
            <div className="flex justify-between items-center mb-4">
              <h2 className="text-lg font-semibold">Daftar Mahasiswa</h2>

              {/* Hanya admin yang bisa tambah data */}
              {role === "admin" && (
                <Button
                  className="bg-blue-600 hover:bg-blue-700"
                  onClick={() => {
                    setEditData(null);
                    setIsModalOpen(true);
                  }}
                >
                  + Tambah Mahasiswa
                </Button>
              )}
            </div>

            <table className="w-full text-sm text-gray-700">
              <thead className="bg-blue-600 text-white">
                <tr>
                  <th className="py-2 px-4 text-left">NIM</th>
                  <th className="py-2 px-4 text-left">Nama</th>
                  <th className="py-2 px-4 text-left">Status</th>
                  {role === "admin" && (
                    <th className="py-2 px-4 text-center">Aksi</th>
                  )}
                </tr>
              </thead>
              <tbody>
                {mahasiswa.length === 0 ? (
                  <tr>
                    <td
                      colSpan={role === "admin" ? 4 : 3}
                      className="text-center py-4 text-gray-500"
                    >
                      Belum ada data mahasiswa
                    </td>
                  </tr>
                ) : (
                  mahasiswa.map((mhs, index) => (
                    <tr key={index} className="even:bg-gray-100 odd:bg-white">
                      <td className="py-2 px-4">{mhs.nim}</td>
                      <td className="py-2 px-4">{mhs.nama}</td>
                      <td className="py-2 px-4">
                        <span
                          className={`font-medium ${
                            mhs.status ? "text-green-600" : "text-red-600"
                          }`}
                        >
                          {mhs.status ? "Aktif" : "Tidak Aktif"}
                        </span>
                      </td>

                      {/* Tombol Edit dan Hapus hanya untuk admin */}
                      {role === "admin" && (
                        <td className="py-2 px-4 text-center">
                          <Button
                            className="bg-yellow-500 hover:bg-yellow-600 mx-1"
                            onClick={() => {
                              setEditData(mhs);
                              setIsModalOpen(true);
                            }}
                          >
                            Edit
                          </Button>
                          <Button
                            className="bg-red-500 hover:bg-red-600 mx-1"
                            onClick={() => deleteMahasiswa(mhs.nim)}
                          >
                            Hapus
                          </Button>
                        </td>
                      )}
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>
        </main>
        <Footer />
      </div>

      {/* Modal Tambah/Edit Mahasiswa */}
      <Modal isOpen={isModalOpen} onClose={() => setIsModalOpen(false)}>
        <FormMahasiswa
          onSubmit={editData ? updateMahasiswa : addMahasiswa}
          editData={editData}
          onClose={() => setIsModalOpen(false)}
        />
      </Modal>
    </div>
  );
};

export default Mhs;
