import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import AuthLayout from "../components/templates/AuthLayout";
import Label from "../components/atoms/Label";
import Input from "../components/atoms/Input";
import Button from "../components/atoms/Button";

const dummyUser = {
  email: "admin@example.com",
  password: "password123",
};

const Login = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  // const handleLogin = (event) => {
  //   event.preventDefault();

  //   if (email === dummyUser.email && password === dummyUser.password) {
  //     localStorage.setItem("isAuthenticated", "true"); // Simpan status login
  //     alert("Login Berhasil! ✅");
  //     navigate("/admin"); // Redirect ke halaman Admin
  //   } else {
  //     setError("Email atau password salah!");
  //   }
  // };
  const handleLogin = (event) => {
    event.preventDefault();

    console.log("Email Input:", email); // Debug email input
    console.log("Password Input:", password); // Debug password input

    if (!email || !password) {
      setError("Email dan password harus diisi!");
      return;
    }

    // Cari pengguna di dummyUser
    const user = dummyUser.find(
      (user) => user.email === email && user.password === password
    );

    console.log("User ditemukan:", user); // Debug apakah user ditemukan

    if (user) {
      localStorage.setItem("isAuthenticated", "true");
      console.log("Login berhasil! ✅"); // Debug login berhasil
      navigate("/admin");
    } else {
      console.log("Login gagal ❌"); // Debug login gagal
      setError("Email atau password tidak ditemukan! Silakan coba lagi.");
    }
  };

  return (
    <AuthLayout>
      <h2 className="text-3xl font-semibold text-center text-blue-600 mb-6">
        Login
      </h2>
      <form onSubmit={handleLogin} className="space-y-4">
        {error && <p className="text-red-500 text-center">{error}</p>}
        <div>
          <Label htmlFor="email">Email</Label>
          <Input
            type="email"
            id="email"
            placeholder="Masukkan email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div>
          <Label htmlFor="password">Password</Label>
          <Input
            type="password"
            id="password"
            placeholder="Masukkan password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <div className="flex justify-between items-center">
          <label className="flex items-center">
            <input type="checkbox" className="mr-2" />
            <span className="text-sm text-gray-600">Ingat saya</span>
          </label>
          <a href="#" className="text-sm text-blue-500 hover:underline">
            Lupa password
          </a>
        </div>
        <Button type="submit" className="w-full bg-blue-600 hover:bg-blue-700">
          Login
        </Button>
      </form>
      <p className="text-sm text-center text-gray-600 mt-4">
        Belum punya akun?{" "}
        <a href="#" className="text-blue-500 hover:underline">
          Daftar
        </a>
      </p>
    </AuthLayout>
  );
};

export default Login;
