import React, { useState } from "react";
import Label from "../atoms/Label";
import Input from "../atoms/Input";
import Button from "../atoms/Button";

const FormMahasiswa = ({ onSubmit }) => {
  const [nim, setNim] = useState("");
  const [nama, setNama] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit({ nim, nama });
  };

  return (
    <form onSubmit={handleSubmit} className="p-4 space-y-4">
      <div>
        <Label htmlFor="nim">NIM</Label>
        <Input type="text" id="nim" value={nim} onChange={(e) => setNim(e.target.value)} required />
      </div>
      <div>
        <Label htmlFor="nama">Nama</Label>
        <Input type="text" id="nama" value={nama} onChange={(e) => setNama(e.target.value)} required />
      </div>
      <Button type="submit" className="bg-blue-600 hover:bg-blue-700 w-full">
        Simpan
      </Button>
    </form>
  );
};

export default FormMahasiswa;
