import Swal from "sweetalert2";

// Konfirmasi sebelum logout
export const confirmLogout = (onConfirm) => {
  Swal.fire({
    title: "Yakin ingin logout?",
    icon: "warning",
    showCancelButton: true,
    confirmButtonText: "Ya, logout",
    cancelButtonText: "Batal",
  }).then((result) => {
    if (result.isConfirmed) {
      onConfirm();
      Swal.fire("Logout berhasil", "", "success");
    }
  });
};

// Konfirmasi sebelum menghapus data mahasiswa
export const confirmDeleteMahasiswa = (onConfirm) => {
  Swal.fire({
    title: "Hapus Mahasiswa?",
    text: "Data mahasiswa yang dihapus tidak dapat dikembalikan!",
    icon: "warning",
    showCancelButton: true,
    confirmButtonText: "Ya, hapus",
    cancelButtonText: "Batal",
  }).then((result) => {
    if (result.isConfirmed) {
      onConfirm();
      Swal.fire("Dihapus!", "Data mahasiswa berhasil dihapus.", "success");
    }
  });
};

// Konfirmasi sebelum menyimpan perubahan data mahasiswa
export const confirmUpdateMahasiswa = (onConfirm) => {
  Swal.fire({
    title: "Simpan Perubahan?",
    text: "Perubahan data mahasiswa akan disimpan.",
    icon: "question",
    showCancelButton: true,
    confirmButtonText: "Ya, simpan",
    cancelButtonText: "Batal",
  }).then((result) => {
    if (result.isConfirmed) {
      onConfirm();
      Swal.fire(
        "Tersimpan!",
        "Perubahan data mahasiswa berhasil disimpan.",
        "success"
      );
    }
  });
};
