import { useState } from "react";
import DashboardTemplate from "../templates/DashboardTemplate";
import Button from "../atoms/Button";
import ModalTambahMahasiswa from "../organisms/ModalTambahMahasiswa";

const AdminMahasiswa = () => {
  const [students, setStudents] = useState([
    { nim: "20211002", nama: "Sabilla Dhea" },
    { nim: "20211003", nama: "Dhea Sabilla" },
  ]);

  const [isModalOpen, setModalOpen] = useState(false);

  const handleAddStudent = (newStudent) => {
    setStudents([...students, newStudent]);
  };

  return (
    <DashboardTemplate>
      <div className="bg-white shadow rounded-lg p-4">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-lg font-semibold">Daftar Mahasiswa</h2>
          <Button
            text="+ Tambah Mahasiswa"
            className="bg-blue-600 text-white"
            onClick={() => setModalOpen(true)}
          />
        </div>
        <table className="w-full text-sm text-gray-700">
          <thead className="bg-blue-600 text-white">
            <tr>
              <th className="py-2 px-4 text-left">NIM</th>
              <th className="py-2 px-4 text-left">Nama</th>
              <th className="py-2 px-4 text-center">Aksi</th>
            </tr>
          </thead>
          <tbody>
            {students.map((student, index) => (
              <tr key={index} className="even:bg-gray-100 odd:bg-white">
                <td className="py-2 px-4">{student.nim}</td>
                <td className="py-2 px-4">{student.nama}</td>
                <td className="py-2 px-4 text-center space-x-2">
                  <Button
                    text="Edit"
                    className="bg-yellow-500 text-white hover:bg-yellow-600"
                  />
                  <Button
                    text="Hapus"
                    className="bg-red-500 text-white hover:bg-red-600"
                  />
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Modal Tambah Mahasiswa */}
      <ModalTambahMahasiswa
        isOpen={isModalOpen}
        onClose={() => setModalOpen(false)}
        onAdd={handleAddStudent}
      />
    </DashboardTemplate>
  );
};

export default AdminMahasiswa;
