import React, { useState, useEffect } from "react";
import Label from "../atoms/Label";
import Input from "../atoms/Input";
import Button from "../atoms/Button";

const FormMahasiswa = ({ onSubmit, editData, onClose }) => {
  const [nim, setNim] = useState("");
  const [nama, setNama] = useState("");
  const [status, setStatus] = useState(true);
  const [oldNim, setOldNim] = useState("");

  useEffect(() => {
    if (editData) {
      setNim(editData.nim);
      setOldNim(editData.nim);
      setNama(editData.nama);
      setStatus(editData.status);
    } else {
      setNim("");
      setNama("");
      setStatus(true);
      setOldNim("");
    }
  }, [editData]);

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!nim || !nama) {
      alert("NIM dan Nama harus diisi!");
      return;
    }

    const updatedData = { nim, nama, status };

    if (editData && oldNim !== nim) {
      onSubmit({ ...updatedData, oldNim });
    } else {
      onSubmit(updatedData);
    }

    // Reset form setelah submit
    setNim("");
    setNama("");
    setStatus(true);
  };

  const handleCancel = () => {
    if (onClose) {
      onClose();
    }
  };

  return (
    <form onSubmit={handleSubmit} className="p-4 space-y-4">
      <div className="flex justify-between items-center mb-3">
        <h2 className="text-lg font-semibold">
          {editData ? "Edit Mahasiswa" : "Tambah Mahasiswa"}
        </h2>
        <button
          className="text-gray-600 hover:text-red-500 text-2xl"
          onClick={handleCancel}
          type="button"
        >
          &times;
        </button>
      </div>

      <div>
        <Label htmlFor="nim">NIM</Label>
        <Input
          type="text"
          id="nim"
          value={nim}
          onChange={(e) => setNim(e.target.value)}
          required
          disabled={!!editData}
        />
      </div>

      <div>
        <Label htmlFor="nama">Nama</Label>
        <Input
          type="text"
          id="nama"
          value={nama}
          onChange={(e) => setNama(e.target.value)}
          required
        />
      </div>

      <div>
        <Label htmlFor="status">Status</Label>
        <select
          id="status"
          value={status}
          onChange={(e) => setStatus(e.target.value === "true")}
          className="w-full px-4 py-2 border rounded focus:outline-none focus:ring focus:ring-blue-300"
        >
          <option value="true">Aktif</option>
          <option value="false">Tidak Aktif</option>
        </select>
      </div>

      <div className="flex justify-end space-x-2 mt-4">
        <Button
          type="button"
          className="bg-gray-400 text-white"
          onClick={handleCancel}
        >
          Batal
        </Button>
        <Button type="submit" className="bg-blue-600 hover:bg-blue-700">
          {editData ? "Simpan Perubahan" : "Simpan"}
        </Button>
      </div>
    </form>
  );
};

export default FormMahasiswa;
