// FormMatkul.jsx
import React from "react";
import Label from "../atoms/Label";
import Input from "../atoms/Input";
import Button from "../atoms/Button";

const FormMatkul = ({ onSubmit, onClose, editData, form, setForm }) => {
  const { kode = "", nama = "", sks = "" } = form || {};

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!form.kode || !form.nama || !form.sks) {
      alert("Semua field harus diisi!");
      return;
    }

    onSubmit(form);
    setForm({ kode: "", nama: "", sks: "" });
  };

  return (
    <form onSubmit={handleSubmit} className="p-4 space-y-4">
      <div className="flex justify-between items-center mb-3">
        <h2 className="text-lg font-semibold">
          {editData ? "Edit Mata Kuliah" : "Tambah Mata Kuliah"}
        </h2>
        <button
          className="text-gray-600 hover:text-red-500 text-2xl"
          onClick={onClose}
          type="button"
        >
          &times;
        </button>
      </div>

      <div>
        <Label htmlFor="kode">Kode</Label>
        <Input
          type="text"
          id="kode"
          name="kode"
          value={kode}
          onChange={handleChange}
          required
          disabled={!!editData} // Kode tidak boleh diubah saat edit
        />
      </div>

      <div>
        <Label htmlFor="nama">Nama Mata Kuliah</Label>
        <Input
          type="text"
          id="nama"
          name="nama"
          value={nama}
          onChange={handleChange}
          required
        />
      </div>

      <div>
        <Label htmlFor="sks">SKS</Label>
        <Input
          type="number"
          id="sks"
          name="sks"
          value={sks}
          onChange={handleChange}
          required
        />
      </div>

      <div className="flex justify-end space-x-2 mt-4">
        <Button
          text="Batal"
          className="bg-gray-400 text-white"
          onClick={onClose}
          type="button"
        />
        <Button type="submit" className="bg-blue-600 hover:bg-blue-700">
          {editData ? "Simpan Perubahan" : "Simpan"}
        </Button>
      </div>
    </form>
  );
};

export default FormMatkul;
