// src/Utils/Helpers/ToastHelpers.js
import Swal from "sweetalert2";

const Toast = Swal.mixin({
  toast: true,
  position: "top-end",
  showConfirmButton: false,
  timer: 3000,
  timerProgressBar: true,
});

export const toastSuccess = (message) => {
  Toast.fire({
    icon: "success",
    title: message,
  });
};

export const toastError = (message) => {
  Toast.fire({
    icon: "error",
    title: message,
  });
};
